import {
  CREATE_TODO,
  REMOVE_TODO,
  MARK_TODO_AS_COMPLETED,
  LOAD_TODOS_IN_PROGRESS,
  LOAD_TODOS_SUCCESS,
  LOAD_TODOS_FAILURE
} from './actions';

const initialState = {
  isLoading: false,
  data: []
}

export const todos = (state = initialState, action) => {
  const { type, payload } = action;

  switch(type) {
    case CREATE_TODO: {
      const { todo } = payload;
      return {
        ...state,
        data: state.data.concat(todo)
      };
    }
    case REMOVE_TODO: {
      const { todo: todoToRemove } = payload;
      return {
        ...state,
        data: state.data.filter(todo => todo.id !== todoToRemove.id)
      };
    }
    case MARK_TODO_AS_COMPLETED: {
      const { todo: updatedTodo } = payload;
      return {
        ...state,
        data: state.data.map(todo => {
          if (todo.id === updatedTodo.id) {
            return updatedTodo;
          }
          return todo;
        })
      };
    }
    case LOAD_TODOS_SUCCESS: {
      const { todos } = payload;
      return {
        ...state,
        isLoading: false,
        data: todos
      };
    }
    case LOAD_TODOS_IN_PROGRESS: {
      return {
        ...state,
        isLoading: true
      }
    }
    case LOAD_TODOS_FAILURE: {
      return {
        ...state,
        isLoading: false
      }
    }
    default:
      return state;
  }
};

export const CREATE_TODO = 'CREATE_TODO';

export const createTodo = todo => ({
  type: CREATE_TODO,
  payload: { todo }
});

export const REMOVE_TODO = 'REMOVE_TODO';

export const removeTodo = todo => ({
  type: REMOVE_TODO,
  payload: { todo }
});

export const MARK_TODO_AS_COMPLETED = 'MARK_TODO_AS_COMPLETED';

export const markTodoAsCompleted = todo => ({
  type: MARK_TODO_AS_COMPLETED,
  payload: { todo }
});

export const LOAD_TODOS_IN_PROGRESS = 'LOAD_TODOS_IN_PROGRESS';

export const loadTodosInProgress = () => ({
  type: LOAD_TODOS_IN_PROGRESS
});

export const LOAD_TODOS_SUCCESS = 'LOAD_TODOS_SUCCESS';

export const loadTodosSuccess = todos => ({
  type: LOAD_TODOS_SUCCESS,
  payload: { todos }
});

export const LOAD_TODOS_FAILURE = 'LOAD_TODOS_FAILURE';

export const loadTodosFailure = () => ({
  type: LOAD_TODOS_FAILURE
});

import { todos } from "./reducers";

import { createSelector } from 'reselect';

export const getTodos = state => state.todos.data;
export const getTodosLoading = state => state.todos.isLoading;

// reselect prevent re-computing the function
// unless theres a change in the selectors

export const getIncompleteTodos = createSelector(
  getTodos,
  (todos) => todos.filter(todos => !todos.isCompleted)
);

export const getCompleteTodos = createSelector(
  getTodos,
  (todos) => todos.filter(todos => todos.isCompleted)
);

import {
  loadTodosInProgress,
  loadTodosSuccess,
  loadTodosFailure,
  createTodo,
  removeTodo,
  markTodoAsCompleted
} from './actions';

export const displayAlert = text => {
  return (text) => {
    alert(text);
  };
};

export const loadTodos = () => async (dispatch, getState) => {
  try {
    dispatch(loadTodosInProgress());
    const response = await fetch('http://localhost:8080/todos');
    const todos = await response.json();
    dispatch(loadTodosSuccess(todos));
  } catch (error) {
    dispatch(loadTodosFailure());
    dispatch(displayAlert(error))
  }
};

export const addTodoRequest = text => async dispatch => {
  try {
    const body = JSON.stringify({ text });
    const response = await fetch('http://localhost:8080/todos', {
      headers: {
        'Content-type': 'application/json'
      },
      method: 'post',
      body
    });
    const todo = await response.json();
    dispatch(createTodo(todo));
  } catch (error) {
    dispatch(displayAlert(error));
  }
};

export const markTodoAsCompletedRequest = id => async dispatch => {
  try {
    const response = await fetch (`http://localhost:8080/todos/${id}/completed`, {
      method: 'post'
    });
    const updatedTodo = await response.json();
    dispatch(markTodoAsCompleted(updatedTodo))
  } catch (error) {
    dispatch(displayAlert(error));
  }
}

export const removeTodoRequest = id => async dispatch => {
  try {
    const response = await fetch(`http://localhost:8080/todos/${id}`, {
      method: 'delete'
    });
    const removedTodo = await response.json();
    dispatch(removeTodo(removedTodo));
  } catch (error) {
    dispatch(displayAlert(error));
  }
};

import React from 'react';
import styled from 'styled-components';

const TodoItemContainer = styled.div`
  background: #FFF;
  border-radius: 8px;
  margin-top: 8px;
  padding: 16px;
  position: relative;
  box-shadow: 0 4px 8px grey;
`;

export const getBorderStyledForDate = (startingDate, currentDate) => (
  startingDate > new Date(currentDate - 8640000 * 5)
    ? 'none'
    : '2px solid red'
);

const TodoItemContainerWithWarning = styled(TodoItemContainer)`
  border-bottom: ${props => getBorderStyledForDate(new Date(props.createdAt), Date.now())};
`;

const ButtonsContainer = styled.div`
  position: absolute;
  right: 12px;
  bottom: 12px
`;

const Button = styled.button`
  font-size: 16px;
  padding: 8px;
  border: none;
  border-radius: 8px;
  outline: none;
  cursor: pointer;
  display: inline-block;
`;

const CompletedButton = styled(Button)`
  background-color: #22ee22;
`;

const RemoveButton = styled(Button)`
  background-color: #ee2222;
  margin-left: 8px;
`;


const TodoListItem = ({ todo, onRemovePressed, onCompletedPressed }) => {
  const Container = todo.isCompleted ? TodoItemContainer : TodoItemContainerWithWarning;

  return (
    <Container createdAt={todo.createdAt}>
      <h3>{todo.text}</h3>
      <p>Created at:&nbsp;
        {(new Date(todo.createdAt)).toLocaleDateString()}
      </p>
      <ButtonsContainer>
        {
          (todo.isCompleted)
            ? null
            : <CompletedButton
                className="completed-button"
                onClick={() => onCompletedPressed(todo.id)}>
                Mark as Completed
              </CompletedButton>
        }
        <RemoveButton
          className="remove-button"
          onClick={() => onRemovePressed(todo.id)}>
          Remove
        </RemoveButton>
      </ButtonsContainer>
    </Container>
  );
};

export default TodoListItem;
